import { createStore } from 'redux'

// Áî®Êù•ËÆ∞ÂΩï old state
let recordState
// ÂàùÂßãÂåñ state
const initialState = []

// ÂàõÂª∫ reducer
const reducer = function (state = initialState, action) {
    recordState = state
    switch (action.type) {
        case 'addBook':
            console.log(action)
            return [
                ...state,
                {
                    bookId: action.info.bookId,
                    bookName: `<<${action.info.bookName}>>`
                }
            ]
        case 'delBook':
            return state.filter((book) => book.bookId !== action.info.bookId)
        default:
            return [
                ...state
            ]
    }
}

// ÂàõÂª∫ store
const store = createStore(reducer)

// Ëé∑ÂèñÊ∑ªÂä†‰π¶Á±çÁöÑÂÖÉÁ¥†
const addBook = document.getElementById('addBook');
// Ëé∑ÂèñÂà†Èô§‰π¶Á±çÁöÑÂÖÉÁ¥†
const delBook = document.getElementById('delBook');
// Ëé∑ÂèñÂ±ïÁ§∫‰π¶Á±çÂàóË°®ÁöÑÂÖÉÁ¥†
const bookList = document.getElementById('bookList');

// ÂàõÂª∫Ê∑ªÂä†‰π¶Á±çÁöÑÊåâÈíÆ
const addBookBtn = document.createElement('button');
// ÂàõÂª∫Ê∑ªÂä†‰π¶Á±çÁöÑinputÊ°ÜÔºåËæìÂÖ•‰π¶ÂêçÊ∑ªÂä†‰π¶Á±ç
