const bookIdInput = document.createElement('input');

addBookBtn.innerText = "Ê∑ªÂä†"
delBookBtn.innerText = "Âà†Èô§"

addBookBtn.addEventListener('click', addBookFn)
delBookBtn.addEventListener('click', delBookFn)

