// ÂÆö‰πâ‰∏Ä‰∏™ÁîüÊàêÂô®ÂáΩÊï∞ÁîüÊàê id
function* generateID() {
    let id = 0;
    while (true) {
        yield id++
    }
}

const generateId = generateID()
const genBookId = () => generateId.next().value.toString()

// Ê∑ªÂä†‰π¶Á±çÂáΩÊï∞
function addBookFn() {
    const bookName = bookNameInput.value
    if (bookName) {
        const bookId = genBookId()
        bookNameInput.value = ''
        const action = {
            type: 'addBook',
            info: {
                bookId,
                bookName
            }
        }
        store.dispatch(action)
    }
}

// Âà†Èô§‰π¶Á±çÂáΩÊï∞
function delBookFn() {
    const bookId = bookIdInput.value
    if (bookId) {
        bookIdInput.value = ''
        const action = {
            type: 'delBook',
            info: {
                bookId: bookId
            }
        }
        store.dispatch(action)
    }
}

// Â¶ÇÊûú state ÂèëÁîüÂèòÂåñÔºåÊâìÂç∞ÊóßÁöÑ state ÂíåÊñ∞ÁöÑ state
